mapper.delete:
delete from follow_groups
where user_id = #{userId,jdbcType=BIGINT}
  and group_id = #{groupId,jdbcType=BIGINT}

mapper.insert:
insert into follow_groups (user_id, group_id, created
  )
values (#{userId,jdbcType=BIGINT}, #{groupId,jdbcType=BIGINT}, #{created,jdbcType=TIMESTAMP}
  )

mapper.update:
update follow_groups
set created = #{created,jdbcType=TIMESTAMP}
where user_id = #{userId,jdbcType=BIGINT}
  and group_id = #{groupId,jdbcType=BIGINT}

mapper.select:
select user_id, group_id, created
from follow_groups
where user_id = #{userId,jdbcType=BIGINT}
  and group_id = #{groupId,jdbcType=BIGINT}
