mapper.delete:
delete from follow_posts
where user_id = #{userId,jdbcType=BIGINT}
  and post_id = #{postId,jdbcType=BIGINT}

mapper.insert:
insert into follow_posts (user_id, post_id, created
  )
values (#{userId,jdbcType=BIGINT}, #{postId,jdbcType=BIGINT}, #{created,jdbcType=TIMESTAMP}
  )

mapper.update:
update follow_posts
set created = #{created,jdbcType=TIMESTAMP}
where user_id = #{userId,jdbcType=BIGINT}
  and post_id = #{postId,jdbcType=BIGINT}

mapper.select:
select user_id, post_id, created
from follow_posts
where user_id = #{userId,jdbcType=BIGINT}
  and post_id = #{postId,jdbcType=BIGINT}
