mapper.delete:
delete from follow_users
where user_id = #{userId,jdbcType=BIGINT}
  and follow_id = #{followId,jdbcType=BIGINT}

mapper.insert:
insert into follow_users (user_id, follow_id, created
  )
values (#{userId,jdbcType=BIGINT}, #{followId,jdbcType=BIGINT}, #{created,jdbcType=TIMESTAMP}
  )

mapper.update:
update follow_users
set created = #{created,jdbcType=TIMESTAMP}
where user_id = #{userId,jdbcType=BIGINT}
  and follow_id = #{followId,jdbcType=BIGINT}

mapper.select:
select user_id, follow_id, created
from follow_users
where user_id = #{userId,jdbcType=BIGINT}
  and follow_id = #{followId,jdbcType=BIGINT}
