mapper.delete:
delete from sys_roles_menus
where role_id = #{roleId,jdbcType=BIGINT}
  and menu_id = #{menuId,jdbcType=BIGINT}

mapper.insert:
insert into sys_roles_menus (role_id, menu_id, created
  )
values (#{roleId,jdbcType=BIGINT}, #{menuId,jdbcType=BIGINT}, #{created,jdbcType=TIMESTAMP}
  )

mapper.update:
update sys_roles_menus
set created = #{created,jdbcType=TIMESTAMP}
where role_id = #{roleId,jdbcType=BIGINT}
  and menu_id = #{menuId,jdbcType=BIGINT}

mapper.select:
select role_id, menu_id, created
from sys_roles_menus
where role_id = #{roleId,jdbcType=BIGINT}
  and menu_id = #{menuId,jdbcType=BIGINT}
