mapper.delete:
delete from sys_roles_permissions
where role_id = #{roleId,jdbcType=BIGINT}
  and permission_id = #{permissionId,jdbcType=BIGINT}

mapper.insert:
insert into sys_roles_permissions (role_id, permission_id, created
  )
values (#{roleId,jdbcType=BIGINT}, #{permissionId,jdbcType=BIGINT}, #{created,jdbcType=TIMESTAMP}
  )

mapper.update:
update sys_roles_permissions
set created = #{created,jdbcType=TIMESTAMP}
where role_id = #{roleId,jdbcType=BIGINT}
  and permission_id = #{permissionId,jdbcType=BIGINT}

mapper.select:
select role_id, permission_id, created
from sys_roles_permissions
where role_id = #{roleId,jdbcType=BIGINT}
  and permission_id = #{permissionId,jdbcType=BIGINT}
